const AuthReducer = (state, action) => {
    switch (action.type) {
        case "LOGIN_START":
            return {
                user: null,
                isFetching: true,
                error: false,
                isAuthenticated: false,
            }
        case "LOGIN_SUCCESS":
            return {
                user: action.payload,
                isFetching: false,
                error: false,
                isAuthenticated: true,
            }
        case "LOGIN_FAILURE":
            return {
                user: null,
                isFetching: false,
                error: action.payload,
                isAuthenticated: false,
            }
        case "REGISTER_START":
            return {
                user: null,
                isFetching: true,
                error: false,
                isAuthenticated: false,
            }
        case "REGISTER_SUCCESS":
            return {
                user: null,
                isFetching: false,
                error: false,
                isAuthenticated: false,
            }
        case "REGISTER_FAILURE":
            console.log("failure")
            return {
                user: null,
                isFetching: false,
                error: action.payload,
                isAuthenticated: false,
            }
        case "LOGOUT":
            return {
                user: null,
                isFetching: false,
                error: null,
                isAuthenticated: false,
            }
        case "FOLLOW":
            return {
                ...state,
                user: {
                    ...state.user,
                    followings: [...state.user.followings, action.payload],
                },
            };
        case "UNFOLLOW":
            return {
                ...state,
                user: {
                    ...state.user,
                    followings: state.user.followings.filter(
                        (following) => following !== action.payload
                    ),
                },
            };
        default:
            return state;

    }
}
export default AuthReducer
