import axios from "axios"

export const loginCall = async (userCredential, dispatch, navigate) => {

    dispatch({ type: "LOGIN_START" })
    try {
        const res = await axios.post("http://localhost:9004/api/auth/login", userCredential)
        dispatch({ type: "LOGIN_SUCCESS", payload: res.data })
        console.log(res);
        navigate("/")

    } catch (err) {
        console.log(err.response.data.msg)
        dispatch({ type: "LOGIN_FAILURE", payload: err.response.data.msg })

    }
}

export const userSession = async (dispatch) => {
    dispatch({ type: "USER_SESSION_REQUEST" })
    try {
        const res = await axios.get("http://localhost:9004/api/auth/checkloggedin")
        dispatch({ type: "USER_SESSION_SUCCESS", payload: res.data })
    } catch (err) {
        dispatch({ type: "USER_SESSION_FAILURE" })
    }
}

export const logout = async (dispatch) => {
    try {
        await axios.get("http://localhost:9004/api/auth/logout")
        dispatch({ type: "Logout" })
    } catch (err) {
        console.log(err)
    }
}
