const AuthReducer = (state, action) => {
    switch (action.type) {
        case "LOGIN_START":
            return {
                user: null,
                isFetching: true,
                error: false,
                isAuthenticated: false,
            }
        case "LOGIN_SUCCESS":
            return {
                user: action.payload,
                isFetching: false,
                error: false,
                isAuthenticated: true,
            }
        case "LOGIN_FAILURE":
            return {
                user: null,
                isFetching: false,
                error: action.payload,
                isAuthenticated: false,
            }
        case "LOGOUT":
            return {
                user: null,
                isFetching: false,
                error: null,
                isAuthenticated: false,
            }

    }
}
export default AuthReducer
