import axios from 'axios'
import React, { useContext, useEffect, useState } from 'react'
import { AuthContext } from '../../context/AuthContext'
import { getTokenFromLocalStorage } from '../../lib/common'
import { BASE_URL } from '../../lib/constant'
import Followers from '../Followers/Followers'
import "./Rightbar.css"
function Rightbar({ user }) {
    const token = getTokenFromLocalStorage()
    const [followings, setFollowings] = useState([])
    const [followers, setFollowers] = useState([])
    const { user: loggedInUser } = useContext(AuthContext)
    console.log(user._id)

    const followCall = async () => {
        try {

            const res = await axios.put(`${BASE_URL}/user/follow`, { id: user._id }, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            })
            setFollowings(res.data.friendList)
            setFollowers(res.data.followersList)
            console.log(res)
        } catch (err) {
            console.log(err)
        }

    }
    const getFriends = async () => {
        const res = await axios.get(`${BASE_URL}/user/friends/` + user._id, {
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
        setFollowings(res.data.friendList)
        setFollowers(res.data.followersList)
        console.log(followings)
        console.log(followers)

    }
    useEffect(() => {
        getFriends()
    }, [user, followings])
    return (
        <div className='rightbar'>
            <div className="row details">
                <div className='col-md-6'>
                    {loggedInUser.user.username !== user.username && (
                        <button className='btn btn-primary' onClick={followCall}>Follow</button>
                    )}
                </div>
                <div className='col-md-12'>
                    <b>Email:</b>{user.email}<br />
                    <b>Major:</b> {user.major}
                </div>
                <div className='followers'>
                    <b>Followers:{followers.length}</b>
                    <div className='row'>
                        {followers.length > 0 && (
                            <div>
                                {followers.map((friend) => (<Followers key={friend._id} friends={friend} />))}
                            </div>
                        )}

                    </div>
                </div>
                <div className='following'>
                    <b>Following:{followings.length}</b>
                    <div className='row'>
                        {followings.length > 0 && (
                            <div>
                                {followings.map((friend) => (<Followers key={friend._id} friends={friend} />))}
                            </div>
                        )}

                    </div>
                </div>
            </div>
        </div>
    )
}

export default Rightbar
